import React, { useState } from "react";
import { motion } from "framer-motion";
import API from "../api";
// import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";

export default function AdminCreateQuiz() {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { text: "", options: ["", "", "", ""], correctIndex: 0 }
  ]);

  const addQuestion = () => {
    setQuestions([...questions, { text: "", options: ["", "", "", ""], correctIndex: 0 }]);
  };

  const updateQuestion = (i, field, value, optIndex = null) => {
    setQuestions(prev => {
      const newQ = [...prev];
      if (field === "text") newQ[i].text = value;
      else if (field === "option") newQ[i].options[optIndex] = value;
      else if (field === "correctIndex") newQ[i].correctIndex = parseInt(value) || 0;
      return newQ;
    });
  };

  const removeQuestion = (index) => {
    if (questions.length > 1) {
      setQuestions(questions.filter((_, i) => i !== index));
    }
  };

  const submitQuiz = async () => {
    if (!title.trim()) return alert("Quiz title is required!");
    if (questions.some(q => !q.text.trim() || q.options.some(o => !o.trim()))) {
      return alert("All questions and options are required!");
    }

    try {
      const res = await API.post("/quiz/create", { title, questions });
      alert(`Quiz Created Successfully!\nQuiz Code: ${res.data.code}\nShare this code with students!`);
      window.location.href = "/admin/quizzes";
    } catch (err) {
      alert("Error: " + (err.response?.data?.error || "Failed to create quiz"));
    }
  };

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          style={{
            flex: 1,
            padding: "30px",
            background: "linear-gradient(135deg, #0f172a 0%, #1e293b 100%)",
            minHeight: "100vh",
          }}
        >
          <div style={{ maxWidth: "900px", margin: "0 auto" }}>
            <motion.h1
              initial={{ scale: 0.9 }}
              animate={{ scale: 1 }}
              style={{
                color: "#ff9800",
                textAlign: "center",
                fontSize: "42px",
                marginBottom: "10px",
                fontWeight: "bold",
              }}
            >
              Create New Quiz
            </motion.h1>
            <p style={{ textAlign: "center", color: "#aaa", fontSize: "18px", marginBottom: "30px" }}>
              Design an amazing quiz for your students
            </p>

            {/* Quiz Title */}
            <motion.div
              whileHover={{ scale: 1.02 }}
              style={{
                background: "rgba(255,255,255,0.08)",
                padding: "20px",
                borderRadius: "16px",
                marginBottom: "30px",
                boxShadow: "0 8px 25px rgba(0,0,0,0.3)",
              }}
            >
              <input
                type="text"
                placeholder="Enter Quiz Title (e.g., Science Quiz - Chapter 5)"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                style={{
                  width: "100%",
                  padding: "16px",
                  fontSize: "20px",
                  background: "rgba(255,255,255,0.1)",
                  border: "2px solid #333",
                  borderRadius: "12px",
                  color: "white",
                  outline: "none",
                }}
              />
            </motion.div>

            {/* Questions */}
            {questions.map((q, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: i * 0.1 }}
                style={{
                  background: "rgba(255,255,255,0.05)",
                  padding: "25px",
                  borderRadius: "16px",
                  marginBottom: "25px",
                  boxShadow: "0 10px 30px rgba(0,0,0,0.4)",
                  border: "1px solid rgba(255,255,255,0.1)",
                  position: "relative",
                }}
              >
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "15px" }}>
                  <h3 style={{ color: "#4caf50", fontSize: "20px" }}>Question {i + 1}</h3>
                  {questions.length > 1 && (
                    <button
                      onClick={() => removeQuestion(i)}
                      style={{
                        background: "#d32f2f",
                        color: "white",
                        border: "none",
                        padding: "8px 12px",
                        borderRadius: "8px",
                        cursor: "pointer",
                      }}
                    >
                      Remove
                    </button>
                  )}
                </div>

                <input
                  type="text"
                  placeholder="Enter question here..."
                  value={q.text}
                  onChange={(e) => updateQuestion(i, "text", e.target.value)}
                  style={inputStyle}
                />

                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "15px", marginTop: "15px" }}>
                  {q.options.map((opt, j) => (
                    <div key={j} style={{ position: "relative" }}>
                      <input
                        type="text"
                        placeholder={`Option ${j + 1}`}
                        value={opt}
                        onChange={(e) => updateQuestion(i, "option", e.target.value, j)}
                        style={{
                          ...inputStyle,
                          paddingLeft: "50px",
                          background: j === q.correctIndex ? "rgba(76, 175, 80, 0.3)" : "rgba(255,255,255,0.08)",
                          border: j === q.correctIndex ? "2px solid #4caf50" : "1px solid #444",
                        }}
                      />
                      {j === q.correctIndex && (
                        <span style={{ position: "absolute", left: "15px", top: "14px", fontSize: "18px" }}>Correct</span>
                      )}
                    </div>
                  ))}
                </div>

                <div style={{ marginTop: "15px" }}>
                  <label style={{ color: "#aaa", fontSize: "14px" }}>Correct Option:</label>
                  <select
                    value={q.correctIndex}
                    onChange={(e) => updateQuestion(i, "correctIndex", e.target.value)}
                    style={{
                      marginLeft: "10px",
                      padding: "10px",
                      background: "#333",
                      color: "white",
                      borderRadius: "8px",
                      border: "1px solid #555",
                    }}
                  >
                    {[0, 1, 2, 3].map(n => (
                      <option key={n} value={n}>Option {n + 1}</option>
                    ))}
                  </select>
                </div>
              </motion.div>
            ))}

            {/* Buttons */}
            <div style={{ textAlign: "center", margin: "40px 0" }}>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={addQuestion}
                style={primaryButton}
              >
                Add Question
              </motion.button>

              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={submitQuiz}
                style={{ ...primaryButton, background: "#4caf50", marginLeft: "20px" }}
              >
                Create Quiz
              </motion.button>
            </div>
          </div>
        </motion.div>
      </div>
    </>
  );
}

// Reusable Styles
const inputStyle = {
  width: "100%",
  padding: "14px",
  margin: "8px 0",
  background: "rgba(255,255,255,0.08)",
  border: "1px solid #444",
  borderRadius: "12px",
  color: "white",
  fontSize: "16px",
  outline: "none",
};

const primaryButton = {
  padding: "14px 40px",
  fontSize: "18px",
  background: "#ff9800",
  color: "white",
  border: "none",
  borderRadius: "12px",
  cursor: "pointer",
  fontWeight: "bold",
  boxShadow: "0 8px 20px rgba(255, 152, 0, 0.4)",
};
