import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [name, setName] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        const res = await API.get(`/quiz/code/${code}`);
        setQuiz(res.data);
        setAnswers(new Array(res.data.questions.length).fill(-1)); // Initial answers
        setLoading(false);
      } catch (err) {
        setError("Invalid Quiz Code");
        setLoading(false);
      }
    };
    fetchQuiz();
  }, [code]);

  const handleAnswerChange = (qIndex, optIndex) => {
    const newAnswers = [...answers];
    newAnswers[qIndex] = optIndex;
    setAnswers(newAnswers);
  };

  const submitQuiz = async () => {
    if (!name.trim()) return alert("Enter your name");
    if (answers.some(a => a === -1)) return alert("Answer all questions");

    try {
      const res = await API.post("/quiz/submit", {
        quizCode: code,
        studentName: name,
        answers
      });
      alert(`Score: ${res.data.score} / ${quiz.questions.length}`);
      navigate(`/leaderboard/${code}`);
    } catch (err) {
      alert("Submit failed");
    }
  };

  if (loading) return <div>Loading quiz...</div>;
  if (error) return <div>{error} <button onClick={() => navigate("/student/home")}>Back</button></div>;

  return (
    <div style={{ padding: 40 }}>
      <h1>{quiz.title} (Code: {quiz.code})</h1>
      <input placeholder="Your Name" value={name} onChange={e => setName(e.target.value)} style={{ marginBottom: 20 }} />

      {quiz.questions.map((q, i) => (
        <div key={i} style={{ marginBottom: 30 }}>
          <h3>{i + 1}. {q.text}</h3>
          {q.options.map((opt, j) => (
            <div key={j}>
              <label>
                <input
                  type="radio"
                  name={`q${i}`}
                  checked={answers[i] === j}
                  onChange={() => handleAnswerChange(i, j)}
                />
                {opt}
              </label>
            </div>
          ))}
        </div>
      ))}

      <button onClick={submitQuiz}>Submit Quiz</button>
    </div>
  );
}
