import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";

export default function CreateQuiz() {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { text: "", options: ["", "", "", ""], correctIndex: 0 },
  ]);
  const addQuestion = () => setQuestions([...questions, { text: "", options: ["","","",""], correctIndex: 0 }]);

  const updateQuestionText = (i, val) => {
    const q = [...questions]; q[i].text = val; setQuestions(q);
  };
  const updateOption = (qi, oi, val) => {
    const q = [...questions]; q[qi].options[oi] = val; setQuestions(q);
  };
  const updateCorrect = (qi, idx) => { const q = [...questions]; q[qi].correctIndex = Number(idx); setQuestions(q); };

  const submitQuiz = async () => {
    try {
      const res = await API.post("/quiz/create", { title, questions });
      alert("Quiz created! Code: " + (res.data.code || res.data.quizCode || "—"));
      window.location = "/admin/quizzes";
    } catch (err) {
      alert(err.response?.data?.error || "Create Quiz failed");
    }
  };

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />
        <div className="content">
          <div className="card">
            <h2>Create Quiz</h2>
            <input placeholder="Quiz title" value={title} onChange={(e)=>setTitle(e.target.value)} style={{width:"100%", padding:8, marginTop:12}} />
            {questions.map((q,i)=>(
              <div key={i} style={{ marginTop:16, padding:12, borderRadius:8, background:"rgba(255,255,255,0.02)" }}>
                <input placeholder={`Question ${i+1}`} value={q.text} onChange={(e)=>updateQuestionText(i,e.target.value)} style={{width:"100%", padding:8}} />
                <div style={{ display:"grid", gridTemplateColumns:"1fr 1fr", gap:8, marginTop:8 }}>
                  {q.options.map((opt,oi)=>(
                    <input key={oi} placeholder={`Option ${oi+1}`} value={opt} onChange={(e)=>updateOption(i,oi,e.target.value)} />
                  ))}
                </div>
                <div style={{ marginTop:8 }}>
                  <label>Correct index (0-3): </label>
                  <input type="number" min="0" max="3" value={q.correctIndex} onChange={(e)=>updateCorrect(i,e.target.value)} style={{width:80}} />
                </div>
              </div>
            ))}
            <div style={{ marginTop: 12 }}>
              <button onClick={addQuestion}>Add Question</button>
              <button onClick={submitQuiz} style={{ marginLeft: 8 }}>Submit Quiz</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
