import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";

export default function AdminQuizzesList() {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    API.get("/quiz")
      .then((res) => {
        setQuizzes(res.data || []);
        setLoading(false);
      })
      .catch(() => {
        setQuizzes([]);
        setLoading(false);
      });
  }, []);

  if (loading) return <h2 style={{ padding: 20 }}>Loading quizzes...</h2>;

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />

        <div className="content">
          <div className="card">
            <h2>All Quizzes</h2>

            {quizzes.length === 0 && <p>No quizzes found</p>}

            <ul style={{ marginTop: 20 }}>
              {quizzes.map((q) => (
                <li
                  key={q._id}
                  style={{
                    padding: 10,
                    marginBottom: 15,
                    borderBottom: "1px solid #555",
                  }}
                >
                  <b>{q.title}</b>  
                  <br />
                  Questions: <b>{q.questions?.length || 0}</b>
                  <br />
                  Code: <b>{q.code || "N/A"}</b>

                  <div style={{ marginTop: 10 }}>
                    <a href={`/quizpage/${q._id}`}>
                      <button>View</button>
                    </a>
                  </div>
                </li>
              ))}
            </ul>

          </div>
        </div>

      </div>
    </>
  );
}
