import React, { useState } from "react";
import API from "../api";

export default function CreateQuiz() {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", optionA: "", optionB: "", optionC: "", optionD: "", correct: "" },
  ]);

  const addQuestion = () => {
    setQuestions([
      ...questions,
      { question: "", optionA: "", optionB: "", optionC: "", optionD: "", correct: "" },
    ]);
  };

  const updateQ = (i, field, value) => {
    const copy = [...questions];
    copy[i][field] = value;
    setQuestions(copy);
  };

  const submitQuiz = async () => {
    try {
      const createdBy = "admin";

      const res = await API.post("/quiz/create", {
        title,
        questions,
        createdBy,
      });

      alert("Quiz Created! Code: " + res.data.code);
    } catch (err) {
      alert("Error creating quiz");
    }
  };

  return (
    <div style={{ padding: 40 }}>
      <h2>Create Quiz (Admin)</h2>

      <input
        placeholder="Quiz Title"
        onChange={(e) => setTitle(e.target.value)}
      />
      <br /><br />

      {questions.map((q, i) => (
        <div key={i} style={{ border: "1px solid gray", padding: 20, marginBottom: 20 }}>
          <input
            placeholder="Question"
            onChange={(e) => updateQ(i, "question", e.target.value)}
          /><br /><br />

          <input
            placeholder="Option A"
            onChange={(e) => updateQ(i, "optionA", e.target.value)}
          /><br /><br />

          <input
            placeholder="Option B"
            onChange={(e) => updateQ(i, "optionB", e.target.value)}
          /><br /><br />

          <input
            placeholder="Option C"
            onChange={(e) => updateQ(i, "optionC", e.target.value)}
          /><br /><br />

          <input
            placeholder="Option D"
            onChange={(e) => updateQ(i, "optionD", e.target.value)}
          /><br /><br />

          <input
            placeholder="Correct Option (A/B/C/D)"
            onChange={(e) => updateQ(i, "correct", e.target.value)}
          /><br />
        </div>
      ))}

      <button onClick={addQuestion}>Add Question</button><br /><br />
      <button onClick={submitQuiz}>Create Quiz</button>
    </div>
  );
}
