// TakeQuiz.js (Working Student Quiz Page)
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [name, setName] = useState("");

  useEffect(() => {
    API.get(`/quiz/code/${code}`)
      .then(res => {
        setQuiz(res.data);
        setAnswers(new Array(res.data.questions.length).fill(-1));
      })
      .catch(() => alert("Invalid or Expired Quiz Code"));
  }, [code]);

  const submitQuiz = async () => {
    if (!name.trim()) return alert("Enter your name");
    if (answers.includes(-1)) return alert("Answer all questions");

    try {
      // Assuming you have a submit endpoint
      const res = await API.post(`/quiz/submit`, {
        quizCode: code,
        studentName: name,
        answers
      });
      alert(`Quiz Submitted! Your Score: ${res.data.score}/${quiz.questions.length}`);
      navigate(`/leaderboard/${code}`);
    } catch (err) {
      alert("Submission failed");
    }
  };

  if (!quiz) return <h2>Loading quiz...</h2>;

  return (
    <div style={{ padding: 40, maxWidth: 800, margin: "0 auto" }}>
      <h1>{quiz.title}</h1>
      <input
        placeholder="Your Name"
        value={name}
        onChange={e => setName(e.target.value)}
        style={{ width: "100%", padding: 10, fontSize: 18, marginBottom: 20 }}
      />

      {quiz.questions.map((q, i) => (
        <div key={i} style={{ marginBottom: 30, padding: 15, border: "1px solid #333" }}>
          <h3>{i + 1}. {q.text}</h3>
          {q.options.map((opt, j) => (
            <div key={j} style={{ margin: "10px 0" }}>
              <label>
                <input
                  type="radio"
                  name={`q${i}`}
                  checked={answers[i] === j}
                  onChange={() => {
                    const newAns = [...answers];
                    newAns[i] = j;
                    setAnswers(newAns);
                  }}
                />
                <span style={{ marginLeft: 10 }}>{opt}</span>
              </label>
            </div>
          ))}
        </div>
      ))}

      <button onClick={submitQuiz} style={{ padding: "15px 30px", fontSize: 18, background: "#1976d2", color: "white" }}>
        Submit Quiz
      </button>
    </div>
  );
}
