import React, { useState } from "react";
import { Container, TextField, Button, Typography } from "@mui/material";
import API from "../api";

export default function AdminCreateQuiz() {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { text: "", options: ["", "", "", ""], correctIndex: 0 }
  ]);

  const addQuestion = () => {
    setQuestions([...questions, { text: "", options: ["", "", "", ""], correctIndex: 0 }]);
  };

  const updateQuestion = (i, field, value, optIndex = null) => {
    setQuestions(prevQuestions => {
      const newQuestions = [...prevQuestions];
      const q = { ...newQuestions[i] }; // Deep copy of object

      if (field === "text") {
        q.text = value;
      } else if (field === "option") {
        q.options = [...q.options];
        q.options[optIndex] = value;
      } else if (field === "correctIndex") {
        q.correctIndex = parseInt(value);
      }

      newQuestions[i] = q;
      return newQuestions;
    });
  };

  const submitQuiz = async () => {
    if (!title) return alert("Enter title");
    if (questions.some(q => !q.text || q.options.some(o => !o))) return alert("Fill all questions and options");

    try {
      const res = await API.post("/quiz/create", { title, questions });
      alert("Quiz Created! Code: " + res.data.code);
      // Optional: Redirect to /admin/quizzes
      window.location.href = "/admin/quizzes";
    } catch (err) {
      alert("Error: " + (err.response?.data?.error || "Failed"));
    }
  };

  return (
    <Container>
      <Typography variant="h4" mt={3}>Create Quiz</Typography>

      <TextField fullWidth label="Quiz Title" margin="normal"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />

      {questions.map((q, i) => (
        <div key={i} style={{ marginTop: 20, padding: 10, border: "1px solid #ccc" }}>
          <TextField
            fullWidth
            label={`Question ${i + 1}`}
            margin="normal"
            value={q.text}
            onChange={(e) => updateQuestion(i, "text", e.target.value)}
          />

          {q.options.map((o, j) => (
            <TextField
              key={j}
              fullWidth
              label={`Option ${j + 1}`}
              margin="normal"
              value={o}
              onChange={(e) => updateQuestion(i, "option", e.target.value, j)}
            />
          ))}

          <TextField
            type="number"
            label="Correct Option (0-3)"
            margin="normal"
            fullWidth
            value={q.correctIndex}
            onChange={(e) => updateQuestion(i, "correctIndex", e.target.value)}
          />
        </div>
      ))}

      <Button variant="outlined" onClick={addQuestion}>Add Question</Button>
      <br /><br />
      <Button variant="contained" onClick={submitQuiz}>Submit</Button>
    </Container>
  );
}
