// src/pages/TakeQuiz.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate();
  
  const [question, setQuestion] = useState(null);
  const [light, setLight] = useState("red");
  const [eliminated, setEliminated] = useState(false);
  const [showBlood, setShowBlood] = useState(false);
  const [playersAlive, setPlayersAlive] = useState(0);

  const playerName = localStorage.getItem("playerName") || "Player";

 useEffect(() => {
  socket.emit("joinLobby", { quizCode: code, playerName });

  // AUTO TRIGGER FIRST QUESTION AFTER 2 SECONDS (for instant testing)
  setTimeout(() => {
    if (!question) {
      // Fake a question if admin hasn't started
      setQuestion({
        text: "What is the capital of France?",
        options: ["London", "Berlin", "Paris", "Madrid"],
        correctIndex: 2
      });
      setLight("green");
      setTimeout(() => setLight("red"), 5000);
    }
  }, 2000);

  // Real socket events
  socket.on("nextQuestion", (q) => {
    setQuestion(q);
    setLight("green");
    setTimeout(() => setLight("red"), 5000);
  });

  socket.on("correctAnswer", () => {
    setLight("green");
    setTimeout(() => setLight("red"), 3000);
  });

  socket.on("eliminated", () => {
    setShowBlood(true);
    new Audio("/gunshot.mp3").play();
    setTimeout(() => setEliminated(true), 1500);
  });

  socket.on("updatePlayers", (players) => setPlayersAlive(players.length));

  return () => socket.off();
}, [code] [code, navigate]);

  const answer = (idx) => {
    if (light !== "green") return;
    socket.emit("submitAnswer", { quizCode: code, answer: idx });
  };

  // ELIMINATED SCREEN
  if (eliminated) {
    return (
      <div style={{ background: "#000", height: "100vh", textAlign: "center", paddingTop: "15vh", color: "#ff0044" }}>
        <h1 style={{ fontSize: "120px", textShadow: "0 0 40px #ff0044" }}>ELIMINATED</h1>
        <p style={{ fontSize: "50px" }}>Wrong Answer</p>
        <div style={{ fontSize: "200px" }}></div>
        {showBlood && <div className="blood-splash" />}
      </div>
    );
  }

  // DIRECT QUESTION â€“ NO WAITING
  return (
    <div style={{ background: "#000", minHeight: "100vh", padding: "40px", textAlign: "center" }}>
      {showBlood && <div className="blood-splash" />}

      <div style={{ marginBottom: "30px" }}>
        <h1 className="glow-red" style={{ fontSize: "28px" }}>Players Alive: {playersAlive}</h1>
        <div className={`light-circle ${light === "green" ? "green-light" : "red-light"}`} />
        <h2 style={{ fontSize: "36px", marginTop: "20px" }}>
          {light === "green" ? "GREEN LIGHT â€“ GO!" : "RED LIGHT â€“ STOP!"}
        </h2>
      </div>

      {question ? (
        <motion.div initial={{ scale: 0.8 }} animate={{ scale: 1 }}>
          <h2 style={{ fontSize: "32px", margin: "40px 0", maxWidth: "900px", margin: "0 auto 40px" }}>
            {question.text}
          </h2>

          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "25px", maxWidth: "800px", margin: "0 auto" }}>
            {question.options.map((opt, i) => (
              <motion.button
                key={i}
                whileHover={{ scale: light === "green" ? 1.05 : 1 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => answer(i)}
                disabled={light !== "green"}
                style={{
                  padding: "30px",
                  fontSize: "24px",
                  background: "#111",
                  border: "2px solid #333",
                  borderRadius: "16px",
                  color: "#fff",
                  cursor: light === "green" ? "pointer" : "not-allowed",
                  opacity: light === "green" ? 1 : 0.5,
                  transition: "all 0.3s"
                }}
              >
                {opt}
              </motion.button>
            ))}
          </div>
        </motion.div>
      ) : (
        <h1 style={{ fontSize: "50px", marginTop: "200px" }} className="glow-red">
          GET READY...
        </h1>
      )}

      <audio src="/tension-music.mp3" autoPlay loop />
    </div>
  );
}
