import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";

export default function AdminQuizzesList() {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");  // ← Naya add kiya

  useEffect(() => {
    API.get("/quiz")
      .then((res) => {
        console.log("Quizzes fetched:", res.data);  // ← Ye console me dekho
        setQuizzes(res.data || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching quizzes:", err.response || err);
        setError("Failed to load quizzes: " + (err.response?.data?.error || err.message));
        setQuizzes([]);
        setLoading(false);
      });
  }, []);

  if (loading) return <h2 style={{ padding: 20 }}>Loading quizzes...</h2>;
  if (error) return <h2 style={{ padding: 20, color: "red" }}>{error}</h2>;  // ← Error dikhega

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />

        <div className="content">
          <div className="card">
            <h2>All Quizzes ({quizzes.length})</h2>

            {quizzes.length === 0 ? (
              <p>No quizzes found in database</p>
            ) : (
              <ul style={{ marginTop: 20 }}>
                {quizzes.map((q) => (
                  <li
                    key={q._id}
                    style={{
                      padding: 15,
                      marginBottom: 15,
                      border: "1px solid #444",
                      borderRadius: 8,
                      background: "#222"
                    }}
                  >
                    <b style={{ fontSize: 18 }}>{q.title}</b>
                    <br />
                    <small>Questions: <b>{q.questions?.length || 0}</b></small>
                    <br />
                    <small>Code: <b style={{ color: "#4caf50" }}>{q.code || "N/A"}</b></small>

                    <div style={{ marginTop: 10 }}>
                      <a href={`/quizpage/${q._id}`}>
                        <button style={{ padding: "8px 16px" }}>View Preview</button>
                      </a>
                      <a href={`/quiz/${q.code}`} style={{ marginLeft: 10 }}>
                        <button style={{ padding: "8px 16px", background: "#1976d2" }}>Test as Student</button>
                      </a>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
