import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student");
  const navigate = useNavigate();

  const submit = async () => {
    if (!name || !email || !password) {
      alert("Please fill all fields");
      return;
    }

    try {
      // Step 1: Register
      await API.post("/auth/register", {
        name,
        email,
        password,
        role,
      });

      // Step 2: Auto Login
      const loginRes = await API.post("/auth/login", { email, password });

      // Step 3: Save credentials
      localStorage.setItem("token", loginRes.data.token);
      localStorage.setItem("role", loginRes.data.role);

      // Step 4: Direct to correct dashboard
      if (loginRes.data.role === "admin") {
        navigate("/admin");
      } else {
        navigate("/student");
      }

      alert(`Welcome, ${name}! You're now in the game.`);
    } catch (err) {
      const errorMsg =
        err.response?.data?.error || "Registration failed. Try again.";
      alert(errorMsg);
      console.error("Register error:", err);
    }
  };

  return (
    <div
      style={{
        background: "#000",
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        position: "relative",
        overflow: "hidden",
        fontFamily: "'Orbitron', 'Rajdhani', sans-serif",
      }}
    >
      {/* Subtle Background Glow */}
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          width: "800px",
          height: "800px",
          background: "radial-gradient(circle, rgba(255,105,180,0.15), transparent 70%)",
          transform: "translate(-50%, -50%)",
          filter: "blur(140px)",
          opacity: 0.7,
        }}
      />

      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.8 }}
        style={{
          background: "rgba(15,15,15,0.95)",
          padding: "60px 50px",
          borderRadius: "24px",
          boxShadow: "0 0 80px rgba(255,105,180,0.3)",
          border: "2px solid #333",
          width: "100%",
          maxWidth: "500px",
          textAlign: "center",
          zIndex: 10,
          backdropFilter: "blur(10px)",
        }}
      >
        {/* Title */}
        <motion.h1
          initial={{ y: -40 }}
          animate={{ y: 0 }}
          style={{
            fontSize: "72px",
            fontWeight: "900",
            background: "linear-gradient(45deg, #ff69b4, #ff3366)",
            WebkitBackgroundClip: "text",
            WebkitTextFillColor: "transparent",
            textShadow: "0 0 40px rgba(255,105,180,0.5)",
            margin: "0 0 50px",
            letterSpacing: "4px",
          }}
        >
          REGISTER
        </motion.h1>

        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          {/* Name */}
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={inputStyle}
            onFocus={(e) => Object.assign(e.target.style, focusStyle)}
            onBlur={(e) =>
              Object.assign(e.target.style, {
                border: "1px solid #333",
                boxShadow: "0 4px 15px rgba(0,0,0,0.6)",
              })
            }
          />

          {/* Email */}
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={inputStyle}
            onFocus={(e) => Object.assign(e.target.style, focusStyle)}
            onBlur={(e) =>
              Object.assign(e.target.style, {
                border: "1px solid #333",
                boxShadow: "0 4px 15px rgba(0,0,0,0.6)",
              })
            }
          />

          {/* Password */}
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={inputStyle}
            onFocus={(e) => Object.assign(e.target.style, focusStyle)}
            onBlur={(e) =>
              Object.assign(e.target.style, {
                border: "1px solid #333",
                boxShadow: "0 4px 15px rgba(0,0,0,0.6)",
              })
            }
          />

          {/* Role Select */}
          <select
            value={role}
            onChange={(e) => setRole(e.target.value)}
            style={{
              ...inputStyle,
              color: role === "admin" ? "#ff3366" : "#00ffcc",
              fontWeight: "bold",
            }}
          >
            <option value="student">Student</option>
            <option value="admin">Admin (Danger Zone)</option>
          </select>

          {/* Submit Button */}
          <motion.button
            whileHover={{ scale: 1.05, boxShadow: "0 0 50px #ff69b4" }}
            whileTap={{ scale: 0.95 }}
            onClick={submit}
            style={{
              width: "100%",
              padding: "20px",
              margin: "30px 0",
              background: "#ff69b4",
              color: "white",
              border: "none",
              borderRadius: "12px",
              fontSize: "24px",
              fontWeight: "bold",
              cursor: "pointer",
              boxShadow: "0 0 40px #ff69b4",
              textTransform: "uppercase",
              letterSpacing: "2px",
            }}
          >
            JOIN GAME
          </motion.button>

          {/* Login Link */}
          <p
            onClick={() => navigate("/login")}
            style={{
              color: "#999",
              cursor: "pointer",
              fontSize: "18px",
              marginTop: "20px",
            }}
          >
            Already in the game?{" "}
            <span style={{ color: "#00ffcc", fontWeight: "bold" }}>Login</span>
          </p>
        </motion.div>
      </motion.div>
    </div>
  );
}

// Input Style (Clean & Premium)
const inputStyle = {
  width: "100%",
  padding: "18px 24px",
  margin: "15px 0",
  background: "#0f0f0f",
  border: "1px solid #333",
  borderRadius: "12px",
  color: "#fff",
  fontSize: "18px",
  outline: "none",
  transition: "all 0.3s",
  boxShadow: "0 4px 15px rgba(0,0,0,0.6)",
};

// Focus Effect
const focusStyle = {
  border: "1px solid #ff6b35",
  boxShadow: "0 0 25px rgba(255,107,53,0.3)",
};
