// src/pages/TakeQuiz.js  (100% Working Version)
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        console.log("Fetching quiz with code:", code); // Debug
        const res = await API.get(`/quiz/code/${code}`);
        console.log("Quiz loaded:", res.data); // Debug
        setQuiz(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error loading quiz:", err.response || err);
        setError("Invalid or Expired Quiz Code");
        setLoading(false);
      }
    };

    if (code && code.length === 4) {
      fetchQuiz();
    } else {
      setError("Invalid Code Format");
      setLoading(false);
    }
  }, [code]);

  if (loading) return <div style={{ padding: 40, fontSize: 24 }}>Loading quiz...</div>;

  if (error) {
    return (
      <div style={{ padding: 40, textAlign: "center" }}>
        <h2 style={{ color: "red" }}>{error}</h2>
        <button onClick={() => navigate("/student/home")} style={{ padding: 10, marginTop: 20 }}>
          Back to Enter Code
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: 40, maxWidth: 900, margin: "0 auto" }}>
      <h1 style={{ textAlign: "center", color: "#4caf50" }}>{quiz.title}</h1>
      <p style={{ textAlign: "center", fontSize: 18 }}>Code: <b>{quiz.code}</b></p>

      <div style={{ marginTop: 30 }}>
        {quiz.questions.map((q, i) => (
          <div key={i} style={{ marginBottom: 30, padding: 20, border: "2px solid #333", borderRadius: 10 }}>
            <h3>{i + 1}. {q.text}</h3>
            <div style={{ marginTop: 15 }}>
              {q.options.map((opt, j) => (
                <div key={j} style={{ margin: "8px 0", fontSize: 18 }}>
                  <label>
                    <input type="radio" name={`q${i}`} disabled /> {opt}
                  </label>
                </div>
              ))}
            </div>
            <p style={{ marginTop: 10, color: "#888" }}>
              Correct Answer: Option {q.correctIndex + 1}
            </p>
          </div>
        ))}
      </div>

      <div style={{ textAlign: "center", marginTop: 40 }}>
        <button onClick={() => navigate("/student/home")} style={{ padding: "12px 30px" }}>
          Back
        </button>
      </div>
    </div>
  );
}
