import React, { useEffect, useState } from "react";
import API from "../api";
import { useParams } from "react-router-dom";

export default function QuizPage() {
  const { id } = useParams();
  const [quiz, setQuiz] = useState(null);

  useEffect(() => {
    API.get(`/quiz/id/${id}`).then(res => setQuiz(res.data)).catch(()=>setQuiz(null));
  }, [id]);

  if (!quiz) return <div style={{padding:40}}>Loading...</div>;

  return (
    <div style={{ padding: 40 }}>
      <h2>{quiz.title}</h2>
      <p>Code: {quiz.code || quiz.quizCode || "—"}</p>
      <ol>
        {quiz.questions.map((q,i)=>(
          <li key={i}>
            <b>{q.question || q.text}</b>
            <ul>
              {(q.options || [q.optionA,q.optionB,q.optionC,q.optionD]).map((o,idx)=>(
                <li key={idx}>{o}</li>
              ))}
            </ul>
            <p>Correct: {q.correct || q.answer || q.correctIndex}</p>
          </li>
        ))}
      </ol>
    </div>
  );
}
