import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const submit = async () => {
    try {
      const res = await API.post("/auth/login", { email, password });
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", res.data.role);
      res.data.role === "admin" ? navigate("/admin") : navigate("/student");
    } catch (err) {
      alert(err.response?.data?.error || "Login Failed");
    }
  };

  return (
    <div style={{
      background: "#000",
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontFamily: "'Rajdhani', sans-serif"
    }}>
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        style={{
          background: "rgba(10,10,10,0.98)",
          padding: "60px 50px",
          borderRadius: "20px",
          border: "1px solid #222",
          boxShadow: "0 10px 40px rgba(0,0,0,0.8)",
          width: "100%",
          maxWidth: "480px",
          textAlign: "center"
        }}
      >
        <h1 style={{
          fontSize: "64px",
          fontWeight: "900",
          color: "#ff6b35",
          margin: "0 0 50px",
          letterSpacing: "6px",
          textShadow: "0 4px 10px rgba(255,107,53,0.3)"
        }}>
          LOGIN
        </h1>

        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={inputStyle}
            onFocus={(e) => Object.assign(e.target.style, focusStyle)}
            onBlur={(e) => Object.assign(e.target.style, { border: "1px solid #333", boxShadow: "0 4px 15px rgba(0,0,0,0.6)" })}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={inputStyle}
            onFocus={(e) => Object.assign(e.target.style, focusStyle)}
            onBlur={(e) => Object.assign(e.target.style, { border: "1px solid #333", boxShadow: "0 4px 15px rgba(0,0,0,0.6)" })}
          />

          <motion.button
            whileHover={{ scale: 1.03 }}
            whileTap={{ scale: 0.98 }}
            onClick={submit}
            style={{
              width: "100%",
              padding: "18px",
              margin: "30px 0 20px",
              background: "#ff6b35",
              color: "white",
              border: "none",
              borderRadius: "12px",
              fontSize: "22px",
              fontWeight: "bold",
              cursor: "pointer",
              boxShadow: "0 6px 20px rgba(255,107,53,0.4)"
            }}
          >
            ENTER GAME
          </motion.button>

          <p
            onClick={() => navigate("/register")}
            style={{
              color: "#999",
              cursor: "pointer",
              fontSize: "16px"
            }}
          >
            Don't have an account? <span style={{ color: "#ff6b35", fontWeight: "bold" }}>Join Game</span>
          </p>
        </div>
      </motion.div>
    </div>
  );
}
