// src/pages/TakeQuiz.js
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { Player } from "@lottiefiles/react-lottie-player";
import API from "../api";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate();

  const [quiz, setQuiz] = useState(null);
  const [name, setName] = useState("");
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [eliminated, setEliminated] = useState(false);
  const [lightState, setLightState] = useState("green"); // Green / Red Light

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        const res = await API.get(`/quiz/code/${code}`);
        setQuiz(res.data);
        setAnswers(new Array(res.data.questions.length).fill(-1));
        setLoading(false);
      } catch (err) {
        alert("Invalid Quiz Code!");
        navigate("/");
      }
    };
    fetchQuiz();

    // Join survival room
    socket.emit("joinSurvival", { quizCode: code, playerName: name || "Player" });

    // Eliminated?
    socket.on("eliminated", () => {
      setEliminated(true);
      new Audio("https://assets.mixkit.co/sfx/preview/mixkit-alarm-buzzer-1005.mp3").play();
      setTimeout(() => navigate("/eliminated"), 3000);
    });

    return () => socket.off("eliminated");
  }, [code, navigate, name]);

  const selectAnswer = (qIndex, optionIndex) => {
    setLightState("red");
    setTimeout(() => setLightState("green"), 800);

    const newAnswers = [...answers];
    newAnswers[qIndex] = optionIndex;
    setAnswers(newAnswers);

    // Send answer to server
    socket.emit("submitAnswer", {
      quizCode: code,
      questionIndex: qIndex,
      selectedOption: optionIndex,
    });
  };

  if (loading) return <div style={{ color: "white", fontSize: "30px", textAlign: "center", padding: "100px" }}>Loading Quiz...</div>;
  if (eliminated) return <EliminatedScreen />;

  return (
    <div style={{ minHeight: "100vh", background: "#0f172a", color: "white", padding: "20px" }}>
      {/* Green / Red Light */}
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <Player
          autoplay
          loop
          src={lightState === "green"
            ? "https://assets5.lottiefiles.com/packages/lf20_7fwv0z3d.json"
            : "https://assets5.lottiefiles.com/packages/lf20_7fwv0z3d.json"}
          style={{ height: "150px" }}
        />
        <h2>{lightState === "green" ? "GREEN LIGHT - Answer Fast!" : "RED LIGHT - Wait!"}</h2>
      </div>

      <h1 style={{ textAlign: "center", color: "#ff9800" }}>{quiz.title}</h1>

      <input
        type="text"
        placeholder="Your Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ width: "100%", padding: "12px", margin: "20px 0", borderRadius: "8px" }}
      />

      {quiz.questions.map((q, i) => (
        <motion.div
          key={i}
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          style={{ margin: "30px 0", padding: "20px", background: "rgba(255,255,255,0.05)", borderRadius: "12px" }}
        >
          <h3>Q{i + 1}. {q.text}</h3>
          {q.options.map((opt, j) => (
            <motion.button
              key={j}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => selectAnswer(i, j)}
              disabled={answers[i] !== -1}
              style={{
                display: "block",
                width: "100%",
                margin: "10px 0",
                padding: "15px",
                background: answers[i] === j ? (j === q.correctIndex ? "#4caf50" : "#ef4444") : "#333",
                color: "white",
                border: "none",
                borderRadius: "10px",
                fontSize: "18px",
              }}
            >
              {opt} {answers[i] === j && (j === q.correctIndex ? "Correct" : "Wrong")}
            </motion.button>
          ))}
        </motion.div>
      ))}
    </div>
  );
}

// Eliminated Screen
function EliminatedScreen() {
  return (
    <div style={{ height: "100vh", background: "#000", color: "red", textAlign: "center", paddingTop: "100px" }}>
      <Player autoplay loop={false} src="https://assets4.lottiefiles.com/packages/lf20_jgxaqypp.json" style={{ height: "300px" }} />
      <h1 style={{ fontSize: "60px" }}>ELIMINATED!</h1>
      <p style={{ fontSize: "30px" }}>Wrong Answer!</p>
    </div>
  );
}
