// Inside student view map
{data.map((sub, i) => {
  const totalQuestions = sub.quiz?.questions?.length || 1;
  const percentage = ((sub.score / totalQuestions) * 100).toFixed(1);

  // Check if Top 10%
  const isTopPerformer = percentage >= 90;

  return (
    <div key={i} style={{ padding: 20, marginBottom: 20, border: "2px solid #333", borderRadius: 12, position: "relative" }}>
      {isTopPerformer && (
        <div style={{
          position: "absolute",
          top: -12,
          right: 15,
          background: "#FFD700",
          color: "black",
          padding: "6px 16px",
          borderRadius: 20,
          fontWeight: "bold",
          fontSize: 14,
          boxShadow: "0 4px 10px rgba(255,215,0,0.4)"
        }}>
          Top Performer
        </div>
      )}

      <h3>{sub.quiz?.title || "Unknown Quiz"}</h3>
      <p><strong>Code:</strong> {sub.quiz?.code}</p>
      <p><strong>Your Score:</strong> {sub.score} / {totalQuestions} ({percentage}%)</p>
      <p><strong>Date:</strong> {new Date(sub.createdAt).toLocaleDateString()}</p>

      {/* Rank Circle */}
      <div style={{
        position: "absolute",
        top: 15,
        left: -20,
        width: 50,
        height: 50,
        background: isTopPerformer ? "#FFD700" : "#1976d2",
        color: "white",
        borderRadius: "50%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        fontSize: 20,
        fontWeight: "bold",
        boxShadow: "0 4px 15px rgba(0,0,0,0.5)"
      }}>
        #{i + 1}
      </div>
    </div>
  );
})}
