import React from "react";
import { Link } from "react-router-dom";

export default function Sidebar() {
  const role = localStorage.getItem("role");

  return (
    <div style={{
      width: 220, background: "rgba(255,255,255,0.03)", color: "#fff",
      padding: 20, minHeight: "calc(100vh - 64px)"
    }}>
      <h3>Menu</h3>

      {role === 'admin' ? (
        <>
          <Link to="/admin" style={{ color: "#fff", display: "block", margin: "10px 0" }}>Dashboard</Link>
          <Link to="/admin/create" style={{ color: "#fff", display: "block", margin: "10px 0" }}>Create Quiz</Link>
          <Link to="/admin/quizzes" style={{ color: "#fff", display: "block", margin: "10px 0" }}>All Quizzes</Link>
        </>
      ) : (
        <>
        
          <Link to="/student" style={{ color: "#fff", display: "block", margin: "10px 0" }}>Dashboard</Link>
          <Link to="/student/home" style={{ color: "#fff", display: "block", margin: "10px 0" }}>Enter Quiz Code</Link>
          <Link to="/quizzes" style={{ color: "#fff", display: "block", margin: "10px 0" }}>All Quizzes</Link>
        </>
      )}
    </div>
  );
}
