import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";

export default function CreateQuiz() {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", optionA: "", optionB: "", optionC: "", optionD: "", correct: "" },
  ]);

  const addQuestion = () => {
    setQuestions([
      ...questions,
      { question: "", optionA: "", optionB: "", optionC: "", optionD: "", correct: "" },
    ]);
  };

  const updateQ = (i, field, value) => {
    const copy = [...questions];
    copy[i][field] = value;
    setQuestions(copy);
  };

  const submitQuiz = async () => {
    try {
      const res = await API.post("/quiz/create", {
        title,
        questions,
        createdBy: "admin",
      });

      alert("Quiz Created! Code: " + res.data.code);
      window.location = "/admin/quizzes";
    } catch (err) {
      alert(err.response?.data?.error || "Error creating quiz");
    }
  };

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />
        <div className="content">
          <div className="card">
            <h2>Create Quiz</h2>

            <input
              placeholder="Quiz Title"
              onChange={(e) => setTitle(e.target.value)}
              style={{ width: "100%", padding: 8, marginTop: 12 }}
            />

            {questions.map((q, i) => (
              <div key={i} style={{ marginTop: 16, padding: 12, border: "1px solid #555" }}>
                <input
                  placeholder="Question"
                  onChange={(e) => updateQ(i, "question", e.target.value)}
                  style={{ width: "100%", padding: 8 }}
                />

                <input
                  placeholder="Option A"
                  onChange={(e) => updateQ(i, "optionA", e.target.value)}
                />
                <input
                  placeholder="Option B"
                  onChange={(e) => updateQ(i, "optionB", e.target.value)}
                />
                <input
                  placeholder="Option C"
                  onChange={(e) => updateQ(i, "optionC", e.target.value)}
                />
                <input
                  placeholder="Option D"
                  onChange={(e) => updateQ(i, "optionD", e.target.value)}
                />

                <input
                  placeholder="Correct Option (A/B/C/D)"
                  onChange={(e) => updateQ(i, "correct", e.target.value)}
                />
              </div>
            ))}

            <button onClick={addQuestion}>Add Question</button>
            <button onClick={submitQuiz} style={{ marginLeft: 8 }}>
              Create Quiz
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
