import React, { useEffect, useState } from "react";
import API from "../api";
import { useParams } from "react-router-dom";

export default function Leaderboard() {
  const { code } = useParams();
  const [data, setData] = useState([]);

  useEffect(() => {
    API.get(`/quiz/leaderboard/${code}`).then((res) => {
      setData(res.data);
    });
  }, [code]);

  return (
    <div style={{ padding: 40 }}>
      <h2>Leaderboard - Quiz {code}</h2>

      {data.length === 0 && <p>No results yet</p>}

      {data.map((s, i) => (
        <div key={i} style={{ padding: 10, borderBottom: "1px solid gray" }}>
          <b>{s.studentName}</b> - Score: {s.score}
        </div>
      ))}
    </div>
  );
}
