import React, { useState } from "react";
import { Container, TextField, Button, Typography } from "@mui/material";
import API from "../api";

export default function AdminCreateQuiz() {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { text: "", options: ["", "", "", ""], correctIndex: 0 }
  ]);

  const addQuestion = () => {
    setQuestions([...questions, { text: "", options: ["", "", "", ""], correctIndex: 0 }]);
  };

  const submitQuiz = async () => {
    const res = await API.post("/quiz/create", { title, questions });
    alert("Quiz Created! Code: " + res.data.code);
  };

  return (
    <Container>
      <Typography variant="h4" mt={3}>Create Quiz</Typography>

      <TextField fullWidth label="Quiz Title" margin="normal"
        onChange={(e) => setTitle(e.target.value)}
      />

      {questions.map((q, i) => (
        <div key={i}>
          <TextField
            fullWidth
            label={`Question ${i + 1}`}
            margin="normal"
            onChange={(e) => {
              questions[i].text = e.target.value;
              setQuestions([...questions]);
            }}
          />

          {q.options.map((o, j) => (
            <TextField
              key={j}
              fullWidth
              label={`Option ${j + 1}`}
              margin="normal"
              onChange={(e) => {
                questions[i].options[j] = e.target.value;
                setQuestions([...questions]);
              }}
            />
          ))}

          <TextField
            type="number"
            label="Correct Option (0-3)"
            margin="normal"
            fullWidth
            onChange={(e) => {
              questions[i].correctIndex = parseInt(e.target.value);
              setQuestions([...questions]);
            }}
          />
        </div>
      ))}

      <Button variant="outlined" onClick={addQuestion}>Add Question</Button>
      <br /><br />
      <Button variant="contained" onClick={submitQuiz}>Submit</Button>
    </Container>
  );
}
