import React, { useEffect, useState } from "react";
import API from "../api";
import { useParams, useNavigate } from "react-router-dom";

export default function TakeQuiz() {
  const { code } = useParams();
  const nav = useNavigate();

  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);

  useEffect(() => {
    API.get(`/quiz/${code}`).then((res) => {
      setQuiz(res.data);
      setAnswers(Array(res.data.questions.length).fill(""));
    });
  }, [code]);

  const submit = async () => {
    const name = prompt("Enter Your Name");

    const res = await API.post("/quiz/submit", {
      quizCode: code,
      studentName: name,
      answers,
    });

    alert("Your Score: " + res.data.score);
    nav(`/leaderboard/${code}`);
  };

  if (!quiz) return <h2>Loading...</h2>;

  return (
    <div style={{ padding: 40 }}>
      <h2>{quiz.title}</h2>

      {quiz.questions.map((q, i) => (
        <div key={i} style={{ marginBottom: 25 }}>
          <h4>{q.question}</h4>

          {["A", "B", "C", "D"].map((opt) => (
            <div key={opt}>
              <input
                type="radio"
                name={`q${i}`}
                value={opt}
                onChange={(e) => {
                  const a = [...answers];
                  a[i] = opt;
                  setAnswers(a);
                }}
              />
              <label> {q["option" + opt]} </label>
            </div>
          ))}
        </div>
      ))}

      <button onClick={submit}>Submit Quiz</button>
    </div>
  );
}
