import React, { useEffect, useState } from "react";
import API from "../api";
import { useParams } from "react-router-dom";

export default function Leaderboard() {
  const { code } = useParams();
  const [data, setData] = useState([]);

  useEffect(() => {
    API.get(`/quiz/leaderboard/${code}`).then(res => setData(res.data)).catch(()=>setData([]));
  }, [code]);

  return (
    <div style={{ padding: 40 }}>
      <h2>Leaderboard - {code}</h2>
      {data.length === 0 && <p>No entries yet</p>}
      {data.map((s,i)=>(
        <div key={i} style={{ padding:8, borderBottom:"1px solid rgba(255,255,255,0.04)" }}>
          <b>{s.studentName}</b> â€” Score: {s.score}
        </div>
      ))}
    </div>
  );
}
