// src/components/Sidebar.jsx

import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function Sidebar() {
  const location = useLocation();
  const role = localStorage.getItem("role");

  const isActive = (path) => 
    location.pathname.startsWith(path) ? { color: "#4caf50", fontWeight: "bold" } : {};

  return (
    <div className="sidebar">
      <h3 style={{ padding: "20px 15px", margin: 0, color: "#fff" }}>Menu</h3>

      {role === "admin" ? (
        <>
          <Link to="/admin" style={isActive("/admin")}>Dashboard</Link>
          <Link to="/admin/create" style={isActive("/admin/create")}>Create Quiz</Link>
          <Link to="/admin/quizzes" style={isActive("/admin/quizzes")}>All Quizzes</Link>
          <Link to="/profile" style={isActive("/profile")}>Profile</Link>
        </>
      ) : (
        <>
          <Link to="/student" style={isActive("/student")}>Dashboard</Link>
          <Link to="/student/home" style={isActive("/student/home")}>Enter Quiz Code</Link>
          <Link to="/profile" style={isActive("/profile")}>My Quiz History</Link>
          {/* ALL QUIZZES HATA DIYA STUDENT KE LIYE */}
        </>
      )}

      <div style={{ marginTop: "auto", padding: 15 }}>
        <button 
          onClick={() => {
            localStorage.clear();
            window.location.href = "/login";
          }}
          style={{ width: "100%", padding: 12, background: "#d32f2f", color: "white", border: "none", borderRadius: 8 }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}
