import React, { useEffect, useState } from "react";
import { Container, Typography } from "@mui/material";
import { useParams } from "react-router-dom";
import API from "../api";

export default function Leaderboard() {
  const { id } = useParams();
  const [board, setBoard] = useState([]);

  useEffect(() => {
    (async () => {
      const res = await API.get(`/quiz/${id}/leaderboard`);
      setBoard(res.data);
    })();
  }, [id]);

  return (
    <Container>
      <Typography variant="h4" mt={3}>Leaderboard</Typography>

      {board.map((p) => (
        <Typography key={p.rank} mt={2}>
          #{p.rank} — {p.name} — Score: {p.score} — Time: {p.time}s
        </Typography>
      ))}
    </Container>
  );
}
