import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";

export default function StudentDashboard() {
  const [stats, setStats] = useState({
    totalAttempts: 0,
    bestScore: 0,
    bestQuiz: "N/A",
    averageScore: 0,
  });

  useEffect(() => {
    const load = async () => {
      try {
        const res = await API.get("/quiz/user/submissions");
        const subs = res.data || [];
        if (subs.length === 0) return;

        const scores = subs.map(s => ({
          score: s.score,
          total: s.quiz?.questions?.length || 1,
          title: s.quiz?.title
        }));

        const best = scores.reduce((a, b) =>
          a.score / a.total > b.score / b.total ? a : b
        );

        const avg =
          scores.reduce((s, c) => s + c.score / c.total, 0) / scores.length;

        setStats({
          totalAttempts: subs.length,
          bestScore: Math.round((best.score / best.total) * 100),
          bestQuiz: best.title,
          averageScore: Math.round(avg * 100),
        });
      } catch (err) {
        console.error("Error loading stats:", err);
      }
    };
    load();
  }, []);

  return (
    <>
      <Navbar />

      <div className="container-row">
        <Sidebar />

        {/* CENTERED CONTENT */}
        <motion.div
          className="content"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          <div className="dashboard-card">
            <motion.div
              animate={{ rotate: [0, 10, -10, 0] }}
              transition={{ duration: 3, repeat: Infinity }}
              style={{ fontSize: "140px", marginBottom: "30px" }}
            >
              ðŸ“Š
            </motion.div>

            <h1 className="dash-title">Student Dashboard</h1>
            <p className="dash-subtitle">View your stats and take quizzes</p>

            <div className="stats-row">
              <p>
                Total Attempts:{" "}
                <strong style={{ color: "#2196f3" }}>
                  {stats.totalAttempts}
                </strong>
              </p>
              <p>
                Best:{" "}
                <strong style={{ color: "#4caf50" }}>
                  {stats.bestScore}%
                </strong>
              </p>
              <p>
                Average:{" "}
                <strong style={{ color: "#ff9800" }}>
                  {stats.averageScore}%
                </strong>
              </p>
            </div>

            <div className="btn-row">
              <Link to="/student/home">
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.95 }}
                  className="btn-blue"
                >
                  Enter Quiz Code
                </motion.button>
              </Link>

              <Link to="/profile">
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.95 }}
                  className="btn-green"
                >
                  View Profile
                </motion.button>
              </Link>
            </div>
          </div>
        </motion.div>
      </div>
    </>
  );
}
