import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../api";
import "../styles/auth.css";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student");
  const navigate = useNavigate();

  const submit = async () => {
    try {
      // Register user in backend
      await API.post("/auth/register", { name, email, password, role });

      // Save user role locally (optional)
      localStorage.setItem("role", role);

      // ðŸš€ Role based navigation
      if (role === "admin") {
        navigate("/admin");   // admin dashboard
      } else {
        navigate("/student"); // student dashboard
      }

    } catch {
      alert("Registration Failed");
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2 style={{ color: "white", marginBottom: "25px" }}>Register</h2>

        <input
          className="auth-input"
          type="text"
          placeholder="Full Name"
          onChange={(e) => setName(e.target.value)}
        />

        <input
          className="auth-input"
          type="text"
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          className="auth-input"
          type="password"
          placeholder="Password"
          onChange={(e) => setPassword(e.target.value)}
        />

        {/* ROLE SELECT */}
        <select
          className="auth-input"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="admin">Admin</option>
          <option value="student">Student</option>
        </select>

        <button className="auth-btn" onClick={submit}>Register</button>

        <p
          onClick={() => navigate("/login")}
          style={{ color: "white", marginTop: "20px", cursor: "pointer" }}
        >
          Already have an account? Login
        </p>
      </div>
    </div>
  );
}
