import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";

export default function AdminQuizzesList() {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(()=>{
    API.get("/quiz").then(res => setQuizzes(res.data)).catch(()=>setQuizzes([]));
  },[]);

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />
        <div className="content">
          <div className="card">
            <h2>All Quizzes</h2>
            {quizzes.length === 0 && <p>No quizzes yet</p>}
            <ul>
              {quizzes.map(q=>(
                <li key={q._id} style={{marginBottom:12}}>
                  <b>{q.title}</b> — {q.questions?.length || 0} Qs — Code: {q.code || q.quizCode || "—"}
                  <div style={{ marginTop:6 }}>
                    <a href={`/quizpage/${q._id}`}><button>View</button></a>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </>
  );
}
