import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import API from "../api";
import { motion } from "framer-motion";

export default function Profile() {
  const role = localStorage.getItem("role");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (role === "admin") loadAdminData();
    else loadStudentData();
  }, [role]);

  const loadStudentData = async () => {
    try {
      const res = await API.get("/quiz/user/submissions");
      setData(res.data || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadAdminData = async () => {
    try {
      const res = await API.get("/quiz");
      const quizzes = res.data;
      const detailed = await Promise.all(
        quizzes.map(async (q) => {
          const subs = await API.get(`/quiz/${q._id}/submissions`).then(r => r.data || []);
          const count = subs.length;
          const avg = count > 0 ? (subs.reduce((s, sub) => s + sub.score, 0) / count).toFixed(1) : 0;
          return { ...q, participants: count, averageScore: avg, submissions: subs };
        })
      );
      setData(detailed);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <h2 style={{ padding: 60, textAlign: "center", color: "#ff6b35" }}>Loading Your Legacy...</h2>;

  return (
    <>
      <Navbar />
      <div style={{
        background: "#000",
        minHeight: "100vh",
        color: "#fff",
        fontFamily: "'Orbitron', sans-serif",
        position: "relative",
        overflow: "hidden"
      }}>
        {/* Background Glow */}
        <div style={{
          position: "absolute",
          top: 0, left: 0, right: 0, bottom: 0,
          background: "radial-gradient(circle at 20% 80%, rgba(255,107,53,0.4), transparent 50%), radial-gradient(circle at 80% 20%, rgba(255,20,147,0.4), transparent 50%)",
          filter: "blur(120px)",
          opacity: 0.6
        }} />

        <div style={{ position: "relative", zIndex: 10, padding: "40px 20px" }}>
          <Sidebar />

          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            style={{ maxWidth: "1000px", margin: "0 auto" }}
          >
            {/* Title */}
            <motion.h1
              initial={{ scale: 0.8 }}
              animate={{ scale: 1 }}
              style={{
                fontSize: "80px",
                textAlign: "center",
                background: "linear-gradient(45deg, #ff6b35, #ff0044)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                textShadow: "0 0 60px rgba(255,107,53,0.8)",
                margin: "40px 0",
                letterSpacing: "8px"
              }}
            >
              {role === "admin" ? "ELIMINATION RECORDS" : "YOUR QUIZ HISTORY"}
            </motion.h1>

            {/* Student View */}
            {role !== "admin" && data.length === 0 && (
              <div style={{ textAlign: "center", padding: "100px 20px" }}>
                <h2 style={{ color: "#ff6b35", fontSize: "36px" }}>No Games Played Yet</h2>
                <p style={{ color: "#666", fontSize: "20px" }}>Enter a quiz code and survive...</p>
              </div>
            )}

            <div style={{ display: "grid", gap: "25px", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))" }}>
              {role === "admin" ? (
                // ADMIN VIEW
                data.map((quiz) => (
                  <motion.div
                    key={quiz._id}
                    whileHover={{ scale: 1.05, boxShadow: "0 0 60px rgba(255,107,53,0.6)" }}
                    style={{
                      background: "rgba(20,20,20,0.95)",
                      padding: "25px",
                      borderRadius: "20px",
                      border: "2px solid #ff6b35",
                      boxShadow: "0 0 40px rgba(255,107,53,0.3)"
                    }}
                  >
                    <h3 style={{ color: "#ff6b35", fontSize: "28px", margin: "0 0 15px" }}>{quiz.title}</h3>
                    <p><strong>Code:</strong> <span style={{ color: "#00ffcc" }}>{quiz.code}</span></p>
                    <p><strong>Eliminations:</strong> {quiz.participants}</p>
                    <p><strong>Avg Survival Score:</strong> {quiz.averageScore}</p>
                    {quiz.submissions[0] && (
                      <div style={{ marginTop: "15px" }}>
                        <strong>Top Survivor:</strong>
                        <p style={{ color: "#00ffcc", margin: "5px 0" }}>
                          {quiz.submissions[0].student?.name || "Unknown"} → {quiz.submissions[0].score} pts
                        </p>
                      </div>
                    )}
                  </motion.div>
                ))
              ) : (
                // STUDENT VIEW
                data.map((sub, i) => {
                  const percentage = Math.round((sub.score / (sub.quiz?.questions?.length || 1)) * 100);
                  const isTop = percentage >= 90;

                  return (
                    <motion.div
                      key={i}
                      initial={{ opacity: 0, x: -50 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: i * 0.1 }}
                      whileHover={{ scale: 1.05 }}
                      style={{
                        background: "rgba(20,20,20,0.95)",
                        padding: "25px",
                        borderRadius: "20px",
                        border: isTop ? "3px solid #FFD700" : "2px solid #333",
                        boxShadow: isTop ? "0 0 60px rgba(255,215,0,0.6)" : "0 0 30px rgba(255,107,53,0.2)",
                        position: "relative",
                        overflow: "hidden"
                      }}
                    >
                      {isTop && (
                        <div style={{
                          position: "absolute",
                          top: -10,
                          right: 10,
                          background: "#FFD700",
                          color: "#000",
                          padding: "8px 20px",
                          borderRadius: "20px",
                          fontWeight: "bold",
                          fontSize: "14px"
                        }}>
                          TOP SURVIVOR
                        </div>
                      )}

                      <h3 style={{ color: "#ff6b35", margin: "0 0 10px" }}>
                        {sub.quiz?.title || "Unknown Game"}
                      </h3>
                      <p><strong>Code:</strong> <span style={{ color: "#00ffcc" }}>{sub.quiz?.code}</span></p>
                      <p><strong>Survival Score:</strong> {sub.score} / {sub.quiz?.questions?.length || "?"} ({percentage}%)</p>
                      <p><strong>Date:</strong> {new Date(sub.createdAt).toLocaleDateString()}</p>

                      <div style={{
                        marginTop: "15px",
                        fontSize: "36px",
                        textAlign: "center",
                        color: percentage >= 80 ? "#00ffcc" : percentage >= 50 ? "#ff6b35" : "#ff0044"
                      }}>
                        Rank #{i + 1}
                      </div>
                    </motion.div>
                  );
                })
              )}
            </div>
          </motion.div>
        </div>
      </div>
    </>
  );
}
