// src/pages/TakeQuiz.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; // â† Critical import
import API from "../api";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate(); // â† Now properly defined

  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [name, setName] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);

  // ... rest of your component

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        const res = await API.get(`/quiz/code/${code}`);
        setQuiz(res.data);
        setAnswers(new Array(res.data.questions.length).fill(-1));
        setLoading(false);
      } catch {
        alert("Invalid Code!");
        navigate("/");
      }
    };
    fetchQuiz();

    socket.emit("joinSurvival", { quizCode: code, playerName: name || "Player" });

    socket.on("eliminated", () => {
      setEliminated(true);
      new Audio("https://assets.mixkit.co/sfx/preview/mixkit-alarm-buzzer-1005.mp3").play();
      setTimeout(() => navigate("/eliminated"), 2500);
    });

    return () => socket.off("eliminated");
  }, [code, navigate, name]);

  const selectAnswer = (qIndex, optionIndex) => {
    setLightState("red");
    setTimeout(() => setLightState("green"), 1000);

    const newAnswers = [...answers];
    newAnswers[qIndex] = optionIndex;
    setAnswers(newAnswers);

    socket.emit("submitAnswer", {
      quizCode: code,
      questionIndex: qIndex,
      selectedOption: optionIndex,
    });
  };

  if (loading) return <div style={{color:"white",fontSize:"30px",textAlign:"center",paddingTop:"100px"}}>Loading Squid Game...</div>;
  if (eliminated) return <EliminatedScreen />;

  return (
    <div style={{ minHeight: "100vh", background: "#0f0c29", color: "white", padding: "20px" }}>
      {/* Green/Red Light */}
      <div style={{ textAlign: "center", margin: "20px 0" }}>
        <div style={{
          fontSize: "120px",
          animation: lightState === "green" ? "pulse 1s infinite" : "none"
        }}>
          {lightState === "green" ? "Green Light" : "Red Light"}
        </div>
        <h2 style={{ color: lightState === "green" ? "#4ade80" : "#ef4444" }}>
          {lightState === "green" ? "RUN! Answer Now!" : "STOP! Don't Move!"}
        </h2>
      </div>

      <h1 style={{ textAlign: "center", color: "#ff9800", fontSize: "36px" }}>{quiz.title}</h1>

      <input
        type="text"
        placeholder="Your Name (Quick!)"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ width: "100%", padding: "15px", fontSize: "18px", borderRadius: "12px", marginBottom: "20px" }}
      />

      {quiz.questions.map((q, i) => (
        <motion.div
          key={i}
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          style={{ margin: "25px 0", padding: "20px", background: "rgba(255,255,255,0.05)", borderRadius: "16px" }}
        >
          <h3 style={{ color: "#fff", fontSize: "22px" }}>Q{i + 1}. {q.text}</h3>
          {q.options.map((opt, j) => (
            <motion.button
              key={j}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => selectAnswer(i, j)}
              disabled={answers[i] !== -1}
              style={{
                display: "block",
                width: "100%",
                margin: "12px 0",
                padding: "18px",
                background: answers[i] === j ? (j === q.correctIndex ? "#22c55e" : "#ef4444") : "#1e293b",
                color: "white",
                border: "2px solid " + (answers[i] === j ? (j === q.correctIndex ? "#22c55e" : "#ef4444") : "#334155"),
                borderRadius: "12px",
                fontSize: "18px",
                fontWeight: "bold",
              }}
            >
              {opt}
              {answers[i] === j && (j === q.correctIndex ? "Correct" : "Wrong")}
            </motion.button>
          ))}
        </motion.div>
      ))}
    </div>
  );
}

function EliminatedScreen() {
  return (
    <div style={{ height: "100vh", background: "#000", color: "#ef4444", textAlign: "center", paddingTop: "80px" }}>
      <h1 style={{ fontSize: "80px", margin: "20px 0" }}>ELIMINATED</h1>
      <p style={{ fontSize: "32px" }}>Wrong Answer!</p>
      <motion.div
        animate={{ rotate: 360 }}
        transition={{ duration: 2, repeat: Infinity }}
        style={{ fontSize: "120px", margin: "40px 0" }}
      >
        Skull
      </motion.div>
      <button onClick={() => navigate("/")} style={{ padding: "15px 40px", background: "#ff9800", color: "white", border: "none", borderRadius: "12px", fontSize: "18px" }}>
        Back to Home
      </button>
    </div>
  );
}
