import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";

export default function StudentHome() {
  const [code, setCode] = useState("");
  const navigate = useNavigate();

  const goToQuiz = () => {
    const trimmed = code.trim();
    if (!trimmed || trimmed.length !== 4 || isNaN(trimmed)) {
      alert("Please enter a valid 4-digit code!");
      return;
    }
    navigate(`/quiz/${trimmed}`);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") goToQuiz();
  };

  return (
    <>
      <Navbar />
      <div className="container-row">
        <Sidebar />

        {/* Main Content - Full Squid Game Vibe */}
        <div style={{
          flex: 1,
          background: "#000",
          minHeight: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          position: "relative",
          overflow: "hidden"
        }}>
          {/* Background Glow Effects */}
          <div style={{
            position: "absolute",
            top: "20%",
            left: "10%",
            width: "400px",
            height: "400px",
            background: "radial-gradient(circle, #ff69b4 10%, transparent 70%)",
            opacity: 0.3,
            filter: "blur(80px)"
          }} />
          <div style={{
            position: "absolute",
            bottom: "15%",
            right: "15%",
            width: "500px",
            height: "500px",
            background: "radial-gradient(circle, #00ffcc 10%, transparent 70%)",
            opacity: 0.3,
            filter: "blur(100px)"
          }} />

          {/* Main Card */}
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8 }}
            style={{
              background: "rgba(15, 15, 15, 0.95)",
              padding: "60px 80px",
              borderRadius: "24px",
              boxShadow: "0 0 80px rgba(255, 107, 53, 0.4), 0 0 120px rgba(255, 105, 180, 0.3)",
              border: "2px solid #333",
              textAlign: "center",
              maxWidth: "600px",
              backdropFilter: "blur(10px)"
            }}
          >
            {/* Title */}
            <motion.h1
              initial={{ y: -50 }}
              animate={{ y: 0 }}
              style={{
                fontSize: "72px",
                fontWeight: "900",
                margin: "0 0 20px 0",
                background: "linear-gradient(45deg, #ff6b35, #f7931e)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                letterSpacing: "6px",
                textShadow: "0 0 40px rgba(255,107,53,0.6)"
              }}
            >
              ENTER QUIZ CODE
            </motion.h1>

            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
              style={{
                color: "#ff6b35",
                fontSize: "22px",
                margin: "20px 0 50px",
                fontWeight: "bold"
              }}
            >
              Only players with the code may enter.
            </motion.p>

            {/* Input + Button */}
            <div style={{ display: "flex", gap: "20px", justifyContent: "center", alignItems: "center", flexWrap: "wrap" }}>
              <motion.input
                whileFocus={{ scale: 1.05, boxShadow: "0 0 30px #00ffcc" }}
                type="text"
                maxLength="4"
                value={code}
                onChange={(e) => setCode(e.target.value.replace(/\D/g, ""))}
                onKeyPress={handleKeyPress}
                placeholder="Enter 4-digit code"
                style={{
                  padding: "20px 30px",
                  fontSize: "28px",
                  width: "280px",
                  background: "#111",
                  color: "#00ffcc",
                  border: "2px solid #333",
                  borderRadius: "12px",
                  textAlign: "center",
                  letterSpacing: "8px",
                  fontWeight: "bold",
                  outline: "none",
                  boxShadow: "0 0 20px rgba(0,255,204,0.2)",
                  transition: "all 0.3s"
                }}
              />

              <motion.button
                whileHover={{ scale: 1.1, boxShadow: "0 0 50px #ff6b35" }}
                whileTap={{ scale: 0.95 }}
                onClick={goToQuiz}
                style={{
                  padding: "20px 50px",
                  fontSize: "24px",
                  background: "#ff6b35",
                  color: "white",
                  border: "none",
                  borderRadius: "12px",
                  fontWeight: "bold",
                  cursor: "pointer",
                  boxShadow: "0 0 40px #ff6b35",
                  textTransform: "uppercase",
                  letterSpacing: "3px"
                }}
              >
                Start Quiz
              </motion.button>
            </div>

            {/* Warning Text */}
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.7 }}
              transition={{ delay: 1 }}
              style={{
                marginTop: "40px",
                color: "#666",
                fontSize: "16px"
              }}
            >
              Warning: Once you enter, there is no turning back.
            </motion.p>
          </motion.div>

          {/* Logout Button - Bottom Left */}
          <motion.button
            whileHover={{ scale: 1.1 }}
            onClick={() => {
              localStorage.clear();
              window.location.href = "/login";
            }}
            style={{
              position: "fixed",
              bottom: "40px",
              left: "40px",
              padding: "16px 40px",
              background: "#ff0044",
              color: "white",
              border: "none",
              borderRadius: "50px",
              fontSize: "18px",
              fontWeight: "bold",
              cursor: "pointer",
              boxShadow: "0 0 40px #ff0044",
              zIndex: 1000
            }}
          >
            Logout
          </motion.button>
        </div>
      </div>
    </>
  );
}
