// src/pages/TakeQuiz.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

export default function TakeQuiz() {
  const { code } = useParams();
  const navigate = useNavigate();

  // All required states
  const [quiz, setQuiz] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [lightState, setLightState] = useState("red"); // red | green
  const [eliminated, setEliminated] = useState(false);
  const [showBlood, setShowBlood] = useState(false);
  const [playersCount, setPlayersCount] = useState(0);

  const playerName = localStorage.getItem("playerName") || "Player";

  useEffect(() => {
    // Join the quiz room
    socket.emit("joinLobby", { quizCode: code, playerName });

    socket.on("nextQuestion", (question) => {
      setCurrentQuestion(question);
      setSelectedAnswer(null);
      setLightState("red");
    });

    socket.on("correctAnswer", () => {
      setLightState("green");
      setTimeout(() => setLightState("red"), 2000);
    });

    socket.on("eliminated", () => {
      setShowBlood(true);
      const gunshot = new Audio("/gunshot.mp3");
      gunshot.play();
      setTimeout(() => {
        setEliminated(true);
      }, 1200);
    });

    socket.on("updatePlayers", (players) => {
      setPlayersCount(players.length);
    });

    socket.on("survivalWinner", (winner) => {
      if (winner.id === socket.id) {
        navigate("/winner");
      } else {
        navigate("/eliminated");
      }
    });

    return () => {
      socket.off("nextQuestion");
      socket.off("correctAnswer");
      socket.off("eliminated");
      socket.off("updatePlayers");
      socket.off("survivalWinner");
    };
  }, [code, navigate]);

  const submitAnswer = (index) => {
    if (lightState !== "green") return; // Only allow during green light
    setSelectedAnswer(index);
    socket.emit("submitAnswer", { quizCode: code, answer: index });
  };

  if (eliminated) {
    return (
      <div style={{ background: "#000", color: "#ff3333", height: "100vh", textAlign: "center", paddingTop: "100px" }}>
        <h1 style={{ fontSize: "100px" }}>ELIMINATED</h1>
        <p style={{ fontSize: "40px" }}>Wrong Answer!</p>
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          style={{ fontSize: "150px", margin: "40px 0" }}
        >
          
        </motion.div>
        {showBlood && <div className="blood-splash" />}
      </div>
    );
  }

  if (!currentQuestion) {
    return (
      <div className="survival-container" style={{ background: "#000", color: "#fff", height: "100vh", textAlign: "center", paddingTop: "100px" }}>
        <h1>Waiting for Question...</h1>
        <p>Players alive: {playersCount}</p>
      </div>
    );
  }

  return (
    <div className="survival-container" style={{ background: "#000", color: "#fff", minHeight: "100vh", padding: "40px" }}>
      {showBlood && <div className="blood-splash" />}

      <div style={{ textAlign: "center", marginBottom: "40px" }}>
        <h1 style={{ fontSize: "32px", marginBottom: "10px" }}>Players Alive: {playersCount}</h1>
        <div className={`light ${lightState === "green" ? "green-light" : "red-light"}`}
             style={{ width: 120, height: 120, borderRadius: "50%", margin: "20px auto", boxShadow: lightState === "green" ? "0 0 60px #22c55e" : "0 0 60px #ef4444" }}>
        </div>
        <p>{lightState === "green" ? "GREEN LIGHT â€“ GO!" : "RED LIGHT â€“ STOP!"}</p>
      </div>

      <motion.div initial={{ scale: 0.8, opacity: 0 }} animate={{ scale: 1, opacity: 1 }}>
        <h2 style={{ fontSize: "28px", marginBottom: "30px" }}>{currentQuestion.text}</h2>

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px", maxWidth: "800px", margin: "0 auto" }}>
          {currentQuestion.options.map((opt, i) => (
            <motion.button
              key={i}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => submitAnswer(i)}
              disabled={lightState !== "green" || selectedAnswer !== null}
              style={{
                padding: "20px",
                fontSize: "20px",
                background: selectedAnswer === i ? (lightState === "green" ? "#22c55e" : "#ef4444") : "#1a1a1a",
                border: "2px solid #444",
                borderRadius: "12px",
                color: "white",
                cursor: lightState === "green" ? "pointer" : "not-allowed",
                opacity: lightState === "green" ? 1 : 0.6
              }}
            >
              {opt}
            </motion.button>
          ))}
        </div>
      </motion.div>

      <audio src="/tension-music.mp3" autoPlay loop />
    </div>
  );
}
